INSERT INTO `wp_options` VALUES ('1110', '_transient_dash_4077549d03da2e451c8b5f002294ff51', '<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2015/11/wordpress-4-4-release-candidate/\'>WordPress 4.4 Release Candidate</a> <span class=\"rss-date\">11/25/2015</span><div class=\"rssSummary\">The release candidate for WordPress 4.4 is now available. RC means we think we’re done, but with millions of users and thousands of plugins and themes, it’s possible we’ve missed something. We hope to ship WordPress 4.4 on Tuesday, December 8, but we need your help to get there. If you haven’t tested 4.4 yet, [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://poststatus.com/interviews-matt-mullenweg/\'>Post Status: Interview with Automattic CEO, Matt Mullenweg, on Calypso and more</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/the-wordpress-theme-directory-replaces-download-counts-with-the-number-of-active-installs\'>WPTavern: The WordPress Theme Directory Replaces Download Counts With the Number of Active Installs</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/justin-tadlock-is-seeking-beta-testers-for-his-first-major-commercial-plugin\'>WPTavern: Justin Tadlock Is Seeking Beta Testers for His First Major Commercial Plugin</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Gói Bổ Xung Tính Năng Phổ Biến:</span> <a href=\'https://wordpress.org/plugins/wpclef/\' class=\'dashboard-news-plugin-link\'>Clef Two-Factor Authentication</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=wpclef&amp;_wpnonce=0807a7694d&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'Clef Two-Factor Authentication\'>Cài đặt</a>)</span></li></ul></div>', 'no');
